contracts/DexPoolFactory.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import './interfaces/IDexPoolFactory.sol';
import './proxy/DexPoolProxy.sol';
import './interfaces/IDexPool.sol';

error InvalidTokenPair();

contract DexPoolFactory is IDexPoolFactory {

    mapping(address => mapping(address => mapping(uint24 => address)))
        public
        override getPool;

    constructor() {}

    function createPool(
        address initialImplementation,
        address tokenA,
        address tokenB,
        uint24 fee
    ) external override returns (address pool) {
        if(tokenA == tokenB) revert InvalidTokenPair();
        (address token0, address token1) = tokenA < tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        if(token0 == address(0)) revert InvalidTokenPair();
        bytes memory data = abi.encodeWithSelector(
            IDexPool.initialize.selector,
            address(this),
            token0,
            token1,
            fee
        );
        pool = address(new DexPoolProxy(initialImplementation, data));
        getPool[token0][token1][fee] = pool;
        // populate mapping in the reverse direction, deliberate choice to avoid the cost of comparing addresses
        getPool[token1][token0][fee] = pool;
        emit PoolCreated(token0, token1, fee, pool);
    }
}

